import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import Navigation from '../components/Navigation';
import { generateRecipeUrl, generateMyRecipesUrl, getUsernameForUrl } from '../utils/urlUtils';
import './Profile.css';

function Profile() {
  const [user, setUser] = useState(null);
  const [profile, setProfile] = useState(null);
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [saving, setSaving] = useState(false);
  const [success, setSuccess] = useState('');

  // Profile form state
  const [fullName, setFullName] = useState('');
  const [username, setUsername] = useState('');
  const [bio, setBio] = useState('');

  const navigate = useNavigate();

  useEffect(() => {
    async function loadUserProfileAndRecipes() {
      try {
        // Check authentication
        const { data: { user: currentUser } } = await supabase.auth.getUser();
        setUser(currentUser);

        if (!currentUser) {
          navigate('/');
          return;
        }

        // Load user profile
        const { data: userProfile, error: profileError } = await supabase
          .from('profiles')
          .select('username, full_name, bio')
          .eq('id', currentUser.id)
          .single();

        if (profileError && profileError.code !== 'PGRST116') {
          console.error('Error loading profile:', profileError);
        } else if (userProfile) {
          setProfile(userProfile);
          setFullName(userProfile.full_name || '');
          setUsername(userProfile.username || '');
          setBio(userProfile.bio || '');
        }

        // Load user's recent recipes
        const { data: recipeData, error: recipeError } = await supabase
          .from('user_recipes')
          .select(`
            id,
            title,
            category,
            ingredients,
            steps,
            image_url,
            created_at
          `)
          .eq('user_id', currentUser.id)
          .order('created_at', { ascending: false })
          .limit(6);

        if (recipeError) {
          throw recipeError;
        }

        setRecipes(recipeData || []);

      } catch (err) {
        console.error('Error loading profile:', err);
        setError(`Failed to load profile: ${err.message}`);
      } finally {
        setLoading(false);
      }
    }

    loadUserProfileAndRecipes();
  }, [navigate]);

  const handleUpdateProfile = async (e) => {
    e.preventDefault();

    if (!user) return;

    try {
      setSaving(true);
      setError('');
      setSuccess('');

      const updates = {
        id: user.id,
        full_name: fullName.trim() || null,
        username: username.trim() || null,
        bio: bio.trim() || null,
        updated_at: new Date().toISOString(),
      };

      const { error: updateError } = await supabase
        .from('profiles')
        .upsert(updates);

      if (updateError) {
        throw updateError;
      }

      // Update local state
      setProfile({
        ...profile,
        full_name: updates.full_name,
        username: updates.username,
        bio: updates.bio
      });

      setSuccess('Profile updated successfully!');

      // Clear success message after 3 seconds
      setTimeout(() => setSuccess(''), 3000);

    } catch (err) {
      console.error('Error updating profile:', err);
      setError(`Failed to update profile: ${err.message}`);
    } finally {
      setSaving(false);
    }
  };

  const handleViewRecipe = (recipe) => {
    const userUsername = getUsernameForUrl(user, profile);
    navigate(generateRecipeUrl(userUsername, recipe));
  };

  const handleViewAllRecipes = () => {
    const userUsername = getUsernameForUrl(user, profile);
    navigate(generateMyRecipesUrl(userUsername));
  };

  const handleSignOut = async () => {
    try {
      const { error } = await supabase.auth.signOut();
      if (error) throw error;
      navigate('/');
    } catch (err) {
      console.error('Error signing out:', err);
      setError(`Failed to sign out: ${err.message}`);
    }
  };

  if (loading) {
    return (
      <>
        <Navigation />
        <div className="profile-page">
          <div className="profile-container">
            <div className="loading-spinner">
              <div className="spinner"></div>
              <p>Loading your profile...</p>
            </div>
          </div>
        </div>
      </>
    );
  }

  if (!user) {
    return (
      <>
        <Navigation />
        <div className="profile-page">
          <div className="profile-container">
            <div className="empty-state">
              <h2>Please Sign In</h2>
              <p>You need to be signed in to view your profile.</p>
            </div>
          </div>
        </div>
      </>
    );
  }

  return (
    <>
      <Navigation />
      <div className="profile-page">
        <div className="profile-container">
          <div className="profile-header">
            <h2 className="page-title">My Profile</h2>
            <button 
              className="sign-out-btn"
              onClick={handleSignOut}
            >
              Sign Out
            </button>
          </div>

          {error && (
            <div className="error-message">
              {error}
            </div>
          )}

          {success && (
            <div className="success-message">
              {success}
            </div>
          )}

          <div className="profile-content">
            <div className="profile-form-section">
              <h3>Profile Information</h3>
              <form onSubmit={handleUpdateProfile} className="profile-form">
                <div className="form-group">
                  <label htmlFor="email">Email</label>
                  <input
                    id="email"
                    type="email"
                    value={user.email || ''}
                    disabled
                    className="form-input disabled"
                  />
                  <small className="form-help">Email cannot be changed</small>
                </div>

                <div className="form-group">
                  <label htmlFor="fullName">Full Name</label>
                  <input
                    id="fullName"
                    type="text"
                    value={fullName}
                    onChange={(e) => setFullName(e.target.value)}
                    placeholder="Enter your full name"
                    className="form-input"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="username">Username</label>
                  <input
                    id="username"
                    type="text"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    placeholder="Enter a username"
                    className="form-input"
                  />
                  <small className="form-help">
                    This will be used in your recipe URLs
                  </small>
                </div>

                <div className="form-group">
                  <label htmlFor="bio">Bio</label>
                  <textarea
                    id="bio"
                    value={bio}
                    onChange={(e) => setBio(e.target.value)}
                    placeholder="Tell us a bit about yourself..."
                    className="form-textarea"
                    rows={4}
                  />
                </div>

                <button 
                  type="submit" 
                  className="update-btn"
                  disabled={saving}
                >
                  {saving ? 'Updating...' : 'Update Profile'}
                </button>
              </form>
            </div>

            <div className="profile-recipes-section">
              <div className="section-header">
                <h3>My Recent Recipes</h3>
                <button 
                  className="view-all-btn"
                  onClick={handleViewAllRecipes}
                >
                  View All Recipes
                </button>
              </div>

              {recipes.length === 0 ? (
                <div className="empty-recipes">
                  <div className="empty-icon">üìù</div>
                  <h4>No recipes yet</h4>
                  <p>Start creating recipes to see them here!</p>
                  <button 
                    className="create-recipe-btn"
                    onClick={handleViewAllRecipes}
                  >
                    Create Your First Recipe
                  </button>
                </div>
              ) : (
                <div className="recipes-preview-grid">
                  {recipes.map((recipe) => (
                    <div 
                      key={recipe.id} 
                      className="recipe-preview-card"
                      onClick={() => handleViewRecipe(recipe)}
                    >
                      {recipe.image_url && (
                        <div className="recipe-preview-image">
                          <img 
                            src={recipe.image_url} 
                            alt={recipe.title}
                            onError={(e) => {
                              e.target.style.display = 'none';
                            }}
                          />
                        </div>
                      )}

                      <div className="recipe-preview-content">
                        <h4 className="recipe-preview-title">{recipe.title}</h4>
                        <p className="recipe-preview-category">{recipe.category}</p>
                        <div className="recipe-preview-meta">
                          <span className="preview-stat">
                            ü•Ñ {recipe.ingredients?.length || 0}
                          </span>
                          <span className="preview-stat">
                            üìã {recipe.steps?.length || 0}
                          </span>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Profile;
