import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import Navigation from '../components/Navigation';
import { generateRecipeUrl, getUsernameForUrl } from '../utils/urlUtils';
import './SavedRecipes.css';

function SavedRecipes() {
  const [user, setUser] = useState(null);
  const [profile, setProfile] = useState(null);
  const [savedRecipes, setSavedRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [unsaveLoading, setUnsaveLoading] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState('All');

  const navigate = useNavigate();

  const categories = ['All', 'Breakfast', 'Dinner', 'Dessert'];

  useEffect(() => {
    async function loadUserAndSavedRecipes() {
      try {
        // Check authentication
        const { data: { user: currentUser } } = await supabase.auth.getUser();
        setUser(currentUser);

        if (!currentUser) {
          navigate('/');
          return;
        }

        // Load user profile
        const { data: userProfile, error: profileError } = await supabase
          .from('profiles')
          .select('username, full_name')
          .eq('id', currentUser.id)
          .single();

        if (profileError && profileError.code !== 'PGRST116') {
          console.error('Error loading profile:', profileError);
        } else {
          setProfile(userProfile);
        }

        // Step 1: Fetch saved recipe IDs
        const { data: saved, error: savedError } = await supabase
          .from('saved_recipes')
          .select('recipe_id')
          .eq('user_id', currentUser.id);

        if (savedError) throw savedError;
        if (!saved || saved.length === 0) {
          setSavedRecipes([]);
          return;
        }

        const recipeIds = saved.map(s => s.recipe_id);

        // Step 2: Fetch recipes using the IDs
        const { data: recipesData, error: recipesError } = await supabase
          .from('recipes')
          .select('id, source_recipe_id')
          .in('id', recipeIds);

        if (recipesError) throw recipesError;

        const sourceRecipeIds = recipesData.map(r => r.source_recipe_id);

        // Step 3: Fetch user_recipes (the actual recipe content) and profiles
        const { data: userRecipesData, error: userRecipesError } = await supabase
          .from('user_recipes')
          .select(`
            *,
            profiles (
              username,
              full_name
            )
          `)
          .in('id', sourceRecipeIds);

        if (userRecipesError) throw userRecipesError;

        // Step 4: Combine the data
        const sourceRecipeMap = new Map(userRecipesData.map(r => [r.id, r]));

        const finalSavedRecipes = recipesData.map(recipe => {
          const userRecipe = sourceRecipeMap.get(recipe.source_recipe_id);
          return {
            id: recipe.id, // This is the 'recipes' table id
            recipe_id: recipe.id, // for handleUnsaveRecipe
            user_recipes: userRecipe,
          };
        }).filter(item => item.user_recipes); // Filter out any that didn't match

        setSavedRecipes(finalSavedRecipes);

      } catch (err) {
        console.error('Error loading saved recipes:', err);
        setError(`Failed to load saved recipes: ${err.message}`);
      } finally {
        setLoading(false);
      }
    }

    loadUserAndSavedRecipes();
  }, [navigate]);

  const handleUnsaveRecipe = async (recipeId, recipeTitle) => {
    if (!window.confirm(`Remove "${recipeTitle}" from your saved recipes?`)) {
      return;
    }

    try {
      setUnsaveLoading(recipeId);

      const { error: deleteError } = await supabase
        .from('saved_recipes')
        .delete()
        .eq('recipe_id', recipeId)
        .eq('user_id', user.id);

      if (deleteError) {
        throw deleteError;
      }

      // Update local state to remove the unsaved recipe
      setSavedRecipes(prevRecipes => prevRecipes.filter(saved => saved.recipe_id !== recipeId));

    } catch (err) {
      console.error('Error removing saved recipe:', err);
      alert(`Failed to remove recipe: ${err.message}`);
    } finally {
      setUnsaveLoading(null);
    }
  };

  const handleViewRecipe = (recipe) => {
    const recipeOwnerUsername = getUsernameForUrl(
      { id: recipe.user_id },
      recipe.profiles
    );
    navigate(generateRecipeUrl(recipeOwnerUsername, recipe));
  };

  if (loading) {
    return (
      <>
        <Navigation />
        <div className="saved-recipes-page">
          <div className="saved-recipes-container">
            <div className="loading-state">
              <div className="spinner"></div>
              <p>Loading your saved recipes...</p>
            </div>
          </div>
        </div>
      </>
    );
  }

  if (!user) {
    return (
      <>
        <Navigation />
        <div className="saved-recipes-page">
          <div className="saved-recipes-container">
            <div className="empty-state">
              <h2>Please Sign In</h2>
              <p>You need to be signed in to view your saved recipes.</p>
            </div>
          </div>
        </div>
      </>
    );
  }

  // Filter recipes by category
  const filteredRecipes = selectedCategory === 'All' 
    ? savedRecipes 
    : savedRecipes.filter(savedItem => 
        savedItem.user_recipes?.category?.toLowerCase() === selectedCategory.toLowerCase()
      );

  return (
    <>
      <Navigation />
      <div className="saved-recipes-page">
        <div className="saved-recipes-container">
          <div className="page-header">
            <div className="header-content">
              <h1 className="page-title">Saved Recipes</h1>
              <p className="page-subtitle">Your personal collection of culinary inspiration.</p>
            </div>
          </div>

          <div className="category-filters">
            {categories.map((category) => (
              <button
                key={category}
                className={`category-btn ${selectedCategory === category ? 'active' : ''}`}
                onClick={() => setSelectedCategory(category)}
              >
                {category}
              </button>
            ))}
          </div>

          {error && (
            <div className="error-message">
              {error}
            </div>
          )}

          {savedRecipes.length === 0 ? (
            <div className="empty-state">
              <div className="empty-icon">üç≥</div>
              <h3>No saved recipes yet</h3>
              <p>Save recipes you love to find them easily later!</p>
              <button 
                className="browse-btn"
                onClick={() => navigate('/')}
              >
                Browse Recipes
              </button>
            </div>
          ) : filteredRecipes.length === 0 ? (
            <div className="empty-state">
              <div className="empty-icon">üîç</div>
              <h3>No {selectedCategory.toLowerCase()} recipes saved</h3>
              <p>Try selecting a different category or save more recipes!</p>
            </div>
          ) : (
            <div className="recipes-grid">
              {filteredRecipes.map((savedItem) => {
                const recipe = savedItem.user_recipes;

                return (
                  <div key={savedItem.id} className="recipe-card" onClick={() => handleViewRecipe(recipe)}>
                    <div className="recipe-image-container">
                      <div className="recipe-image">
                        {recipe.image_url ? (
                          <img 
                            src={recipe.image_url} 
                            alt={recipe.title}
                            onError={(e) => {
                              e.target.src = '/api/placeholder/300/200';
                            }}
                          />
                        ) : (
                          <div className="placeholder-image">
                            <span>üç≥</span>
                          </div>
                        )}
                      </div>
                      <button 
                        className="bookmark-btn saved"
                        onClick={(e) => {
                          e.stopPropagation();
                          handleUnsaveRecipe(savedItem.recipe_id, recipe.title);
                        }}
                        disabled={unsaveLoading === savedItem.recipe_id}
                        title="Remove from saved"
                      >
                        <svg width="24" height="24" viewBox="0 0 24 24" fill="currentColor">
                          <path d="M19 21l-7-5-7 5V5a2 2 0 0 1 2-2h10a2 2 0 0 1 2 2z"/>
                        </svg>
                      </button>
                    </div>

                    <div className="recipe-info">
                      <h3 className="recipe-title">{recipe.title}</h3>
                      <div className="recipe-author">
                        <span className="author-label">By</span>
                        <span className="author-name">
                          {recipe.profiles?.full_name || recipe.profiles?.username || 'Anonymous'}
                        </span>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>
      </div>
    </>
  );
}

export default SavedRecipes;
