import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import Navigation from '../components/Navigation';
import { generateRecipeUrl, generateCreateRecipeUrl, generateEditRecipeUrl, getUsernameForUrl } from '../utils/urlUtils';
import './MyRecipes.css';

function MyRecipes() {
  const [user, setUser] = useState(null);
  const [profile, setProfile] = useState(null);
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [deleteLoading, setDeleteLoading] = useState(null);

  const navigate = useNavigate();
  const { username } = useParams();

  // Helper: Generate subtitle for recipes
  const getRecipeSubtitle = (recipe) => {
    const title = (recipe?.title || '').toLowerCase();
    if (title.includes('pasta')) return 'A rich and flavorful pasta dish.';
    if (title.includes('salad')) return 'A refreshing and healthy salad.';
    if (title.includes('cake') || title.includes('chocolate')) return 'A decadent chocolate dessert.';
    if (title.includes('chili') || title.includes('vegan')) return 'Hearty and packed with flavor.';
    if (recipe?.category) return `A delicious ${recipe.category.toLowerCase()} recipe.`;
    return 'Your delicious homemade recipe.';
  };

  useEffect(() => {
    async function loadUserAndRecipes() {
      try {
        // Check authentication
        const { data: { user: currentUser } } = await supabase.auth.getUser();
        setUser(currentUser);

        if (!currentUser) {
          navigate('/');
          return;
        }

        // Load user profile
        const { data: userProfile, error: profileError } = await supabase
          .from('profiles')
          .select('username, full_name')
          .eq('id', currentUser.id)
          .single();

        if (profileError && profileError.code !== 'PGRST116') {
          console.error('Error loading profile:', profileError);
        } else {
          setProfile(userProfile);
        }

        // Validate that the username in URL matches current user
        const expectedUsername = getUsernameForUrl(currentUser, userProfile);
        if (username !== expectedUsername) {
          navigate(`/${expectedUsername}/my-recipes`);
          return;
        }

        // Load user's recipes
        const { data: recipeData, error: recipeError } = await supabase
          .from('user_recipes')
          .select(`
            id,
            title,
            category,
            ingredients,
            steps,
            image_url,
            created_at,
            updated_at
          `)
          .eq('user_id', currentUser.id)
          .order('created_at', { ascending: false });

        if (recipeError) {
          throw recipeError;
        }

        setRecipes(recipeData || []);

      } catch (err) {
        console.error('Error loading recipes:', err);
        setError(`Failed to load recipes: ${err.message}`);
      } finally {
        setLoading(false);
      }
    }

    loadUserAndRecipes();
  }, [navigate, username]);

  const debugStorageBucket = async () => {
    try {
      // Check both possible bucket names
      const bucketNames = ['recipe-images', 'recipe_images'];

      for (const bucketName of bucketNames) {
        console.log(`\n--- Checking bucket: ${bucketName} ---`);

        const { data, error } = await supabase.storage
          .from(bucketName)
          .list('', { limit: 100 });

        if (error) {
          console.error(`Error listing files in ${bucketName}:`, error);
        } else {
          console.log(`Files in ${bucketName} bucket root:`, data);

          // Also try to list files in user's folder
          const { data: userFiles, error: userError } = await supabase.storage
            .from(bucketName)
            .list(user.id, { limit: 100 });

          if (!userError && userFiles.length > 0) {
            console.log(`Files in ${bucketName}/${user.id} folder:`, userFiles);
          } else if (userError) {
            console.log(`No ${user.id} folder in ${bucketName} or error:`, userError);
          }
        }
      }
    } catch (err) {
      console.error('Debug storage error:', err);
    }
  };

  const handleDeleteRecipe = async (recipeId, recipeTitle) => {
    if (!window.confirm(`Are you sure you want to delete "${recipeTitle}"?`)) {
      return;
    }

    // Debug: List current files in storage
    await debugStorageBucket();

    try {
      setDeleteLoading(recipeId);

      // First, get the recipe to check if it has an image
      const recipeToDelete = recipes.find(recipe => recipe.id === recipeId);

      // Delete the recipe from database
      const { error: deleteError } = await supabase
        .from('user_recipes')
        .delete()
        .eq('id', recipeId)
        .eq('user_id', user.id); // Ensure user can only delete their own recipes

      if (deleteError) {
        throw deleteError;
      }

      // If recipe has an image, delete it from storage
      if (recipeToDelete?.image_url) {
        try {
          console.log('Attempting to delete image:', recipeToDelete.image_url);

          // Extract the file path from the URL
          // URLs can be in different formats:
          // 1. https://[project].supabase.co/storage/v1/object/public/recipe-images/[file-path]
          // 2. https://[project].supabase.co/storage/v1/object/sign/recipe-images/[file-path]
          const url = new URL(recipeToDelete.image_url);
          const pathParts = url.pathname.split('/');

          console.log('URL pathname:', url.pathname);
          console.log('Path parts:', pathParts);

          // Look for both 'recipe-images' and 'recipe_images' bucket names
          let bucketIndex = pathParts.findIndex(part => part === 'recipe-images');
          if (bucketIndex === -1) {
            bucketIndex = pathParts.findIndex(part => part === 'recipe_images');
          }
          console.log('Bucket index:', bucketIndex);

          if (bucketIndex !== -1 && bucketIndex < pathParts.length - 1) {
            const filePath = pathParts.slice(bucketIndex + 1).join('/');
            console.log('Extracted file path:', filePath);

            // Try to delete the file
            // Try both possible bucket names
            let { data: deleteData, error: storageError } = await supabase.storage
              .from('recipe-images')
              .remove([filePath]);

            if (storageError) {
              console.error('Storage deletion error with recipe-images:', storageError);

              // Try with recipe_images bucket name
              const result = await supabase.storage
                .from('recipe_images')
                .remove([filePath]);

              if (result.error) {
                console.error('Storage deletion error with recipe_images:', result.error);
              } else {
                console.log('Image deletion result from recipe_images bucket:', result.data);
                console.log(`Deleted ${result.data?.length || 0} files from recipe_images`);
                deleteData = result.data;
                storageError = null;
              }
            } else {
              console.log('Image deletion result from recipe-images bucket:', deleteData);
              console.log(`Deleted ${deleteData?.length || 0} files from recipe-images`);

              // Verify the file was actually deleted by trying to get its public URL
              setTimeout(async () => {
                const { data: checkData } = await supabase.storage
                  .from('recipe-images')
                  .list('', { search: filePath.split('/').pop() });
                console.log('Files found after deletion attempt:', checkData);
              }, 1000);
            }
          } else {
            console.warn('Could not find recipe-images bucket in URL path');

            // Alternative approach: try to extract filename from URL
            const urlParts = recipeToDelete.image_url.split('/');
            const possibleFilename = urlParts[urlParts.length - 1];

            if (possibleFilename && possibleFilename.includes('.')) {
              console.log('Trying alternative path extraction:', possibleFilename);

              // Try different possible paths and bucket names
              const bucketNames = ['recipe-images', 'recipe_images'];
              const possiblePaths = [
                possibleFilename, // Direct filename (like 'recipe_b81eb452-f14a-463f-837b-9bbe80c8d0ed_1760221654295.png')
                `${user.id}/${possibleFilename}`, // User folder + filename
              ];

              let deleted = false;
              for (const bucketName of bucketNames) {
                if (deleted) break;

                for (const path of possiblePaths) {
                  console.log(`Trying to delete from bucket '${bucketName}' with path:`, path);

                  const { error: altError } = await supabase.storage
                    .from(bucketName)
                    .remove([path]);

                  if (!altError) {
                    console.log(`Successfully deleted from '${bucketName}' using path:`, path);
                    deleted = true;
                    break;
                  } else {
                    console.warn(`Failed to delete from '${bucketName}' using path '${path}':`, altError);
                  }
                }
              }

              if (!deleted) {
                console.error('Failed to delete image using all attempted methods');
              }
            }
          }
        } catch (imageError) {
          console.error('Error deleting recipe image:', imageError);
          // Continue with recipe deletion even if image deletion fails
        }
      }

      // Update local state to remove the deleted recipe
      setRecipes(prevRecipes => prevRecipes.filter(recipe => recipe.id !== recipeId));

    } catch (err) {
      console.error('Error deleting recipe:', err);
      alert(`Failed to delete recipe: ${err.message}`);
    } finally {
      setDeleteLoading(null);
    }
  };

  const handleEditRecipe = (recipe) => {
    const username = getUsernameForUrl(user, profile);
    navigate(generateEditRecipeUrl(username, recipe.id));
  };

  const handleViewRecipe = (recipe) => {
    const username = getUsernameForUrl(user, profile);
    navigate(generateRecipeUrl(username, recipe));
  };

  const handleCreateRecipe = () => {
    const username = getUsernameForUrl(user, profile);
    navigate(generateCreateRecipeUrl(username));
  };

  if (loading) {
    return (
      <>
        <Navigation />
        <div className="my-recipes-page">
          <div className="my-recipes-container">
            <div className="loading-state">
              <div className="spinner"></div>
              <p>Loading your recipes...</p>
            </div>
          </div>
        </div>
      </>
    );
  }

  if (!user) {
    return (
      <>
        <Navigation />
        <div className="my-recipes-page">
          <div className="my-recipes-container">
            <div className="empty-state">
              <h2>Please Sign In</h2>
              <p>You need to be signed in to view your recipes.</p>
            </div>
          </div>
        </div>
      </>
    );
  }

  return (
    <>
      <Navigation />
      <div className="my-recipes-page">
        <div className="my-recipes-container">
          <div className="page-header">
            <h1 className="page-title">My Recipes</h1>
            <button 
              className="add-recipe-btn"
              onClick={handleCreateRecipe}
            >
              + Add Recipe
            </button>
          </div>

          {error && (
            <div className="error-message">
              {error}
            </div>
          )}

          {recipes.length === 0 ? (
            <div className="empty-state">
              <div className="empty-icon">üìù</div>
              <h3>No recipes yet</h3>
              <p>Create your first recipe to get started!</p>
              <button 
                className="add-recipe-btn add-recipe-btn-large"
                onClick={handleCreateRecipe}
              >
                Create Your First Recipe
              </button>
            </div>
          ) : (
            <>
              <div className="my-recipes-grid">
                {recipes.map((recipe) => (
                  <div 
                    key={recipe.id} 
                    className="my-recipe-card"
                    onClick={() => handleViewRecipe(recipe)}
                  >
                    <div className="my-recipe-image-wrap">
                      {recipe.image_url ? (
                        <img 
                          src={recipe.image_url} 
                          alt={recipe.title}
                          loading="lazy"
                        />
                      ) : (
                        <div className="placeholder-image">
                          <span>üç≥</span>
                        </div>
                      )}
                    </div>

                    <h3 className="my-recipe-card-title">{recipe.title}</h3>
                    <p className="my-recipe-card-subtitle">{getRecipeSubtitle(recipe)}</p>

                    <div className="recipe-actions">
                      <button 
                        className="edit-btn"
                        onClick={(e) => {
                          e.stopPropagation();
                          handleEditRecipe(recipe);
                        }}
                        title="Edit recipe"
                      >
                        ‚úèÔ∏è
                      </button>
                      <button 
                        className="delete-btn"
                        onClick={(e) => {
                          e.stopPropagation();
                          handleDeleteRecipe(recipe.id, recipe.title);
                        }}
                        disabled={deleteLoading === recipe.id}
                        title="Delete recipe"
                      >
                        {deleteLoading === recipe.id ? '‚è≥' : 'üóëÔ∏è'}
                      </button>
                    </div>
                  </div>
                ))}
              </div>

              {/* Pagination */}
              <div className="pagination">
                <button className="pagination-btn prev" disabled>
                  &lt;
                </button>
                <button className="pagination-btn active">1</button>
                <button className="pagination-btn">2</button>
                <button className="pagination-btn">3</button>
                <button className="pagination-btn next">
                  &gt;
                </button>
              </div>
            </>
          )}
        </div>
      </div>
    </>
  );
}

export default MyRecipes;
