import React, { useState } from 'react';
import { supabase } from '../supabaseClient';
import './Auth.css';

function Auth({ onClose, onSuccess }) {
  const [isSignUp, setIsSignUp] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [fullName, setFullName] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  // OTP mode (passwordless only)
  const [otpSent, setOtpSent] = useState(false);
  const [otpCode, setOtpCode] = useState('');

  const handleAuth = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setMessage('');

    try {
      if (isSignUp) {
        const { data, error } = await supabase.auth.signUp({
          email,
          password,
          options: {
            data: {
              full_name: fullName,
            }
          }
        });

        if (error) throw error;

        if (data.user) {
          // Create profile record
          const { error: profileError } = await supabase
            .from('profiles')
            .insert([{
              id: data.user.id,
              full_name: fullName,
              email: email,
              username: email.split('@')[0],
            }]);

          if (profileError) {
            console.error('Profile creation error:', profileError);
          }
        }

        setMessage('Check your email for the confirmation link!');
      } else {
        // OTP flow: send code (or magic link depending on project setting)
        const { error } = await supabase.auth.signInWithOtp({
          email,
          options: {
            shouldCreateUser: true,
            emailRedirectTo: window.location.origin
          }
        });

        if (error) throw error;

        setOtpSent(true);
        setMessage('We\'ve sent you a code or magic link. Check your email.');
      }
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleVerifyOtp = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setMessage('');

    try {
      const { data, error } = await supabase.auth.verifyOtp({
        email,
        token: otpCode,
        type: 'email'
      });

      if (error) throw error;

      setMessage('You are signed in!');
      setTimeout(() => {
        onSuccess();
      }, 800);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-overlay">
      <div className="auth-modal">
        <button className="auth-close" onClick={onClose}>×</button>

        <div className="auth-header">
          <h2>{isSignUp ? 'Create Account' : 'Sign In'}</h2>
          <p>
            {isSignUp 
              ? 'Join our recipe community' 
              : 'Welcome back to Recipe App'}
          </p>
        </div>

        {message && <div className="auth-message success">{message}</div>}
        {error && <div className="auth-message error">{error}</div>}

        {/* Sign Up form */}
        {isSignUp ? (
          <form onSubmit={handleAuth} className="auth-form">
            <div className="form-group">
              <label htmlFor="fullName">Full Name</label>
              <input
                id="fullName"
                type="text"
                value={fullName}
                onChange={(e) => setFullName(e.target.value)}
                placeholder="Enter your full name"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                id="email"
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Enter your email"
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input
                id="password"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Create a password"
                minLength={6}
                required
              />
            </div>

            <button 
              type="submit" 
              className="auth-button"
              disabled={loading}
            >
              {loading ? 'Loading...' : 'Sign Up'}
            </button>
          </form>
        ) : (
          // Sign In form (OTP or Password)
          <>
            <div className="form-group" style={{ display: 'flex', gap: '0.5rem' }}>
              <button 
                className="auth-button" 
                type="button" 
                onClick={() => { setAuthMode('otp'); setError(''); setMessage(''); }}
                style={{ flex: 1, opacity: authMode === 'otp' ? 1 : 0.7 }}
              >
                Sign in with OTP
              </button>
              <button 
                className="auth-button" 
                type="button" 
                onClick={() => { setAuthMode('password'); setError(''); setMessage(''); }}
                style={{ flex: 1, opacity: authMode === 'password' ? 1 : 0.7 }}
              >
                Password
              </button>
            </div>

            {authMode === 'password' ? (
              <form onSubmit={handleAuth} className="auth-form">
                <div className="form-group">
                  <label htmlFor="email">Email</label>
                  <input
                    id="email"
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="Enter your email"
                    required
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="password">Password</label>
                  <input
                    id="password"
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Enter your password"
                    minLength={6}
                    required
                  />
                </div>
                <button type="submit" className="auth-button" disabled={loading}>
                  {loading ? 'Loading...' : 'Sign In'}
                </button>
              </form>
            ) : (
              // OTP Flow
              <>
                <form onSubmit={otpSent ? handleVerifyOtp : handleAuth} className="auth-form">
                  <div className="form-group">
                    <label htmlFor="email">Email</label>
                    <input
                      id="email"
                      type="email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      placeholder="Enter your email"
                      required
                    />
                  </div>
                  {otpSent && (
                    <div className="form-group">
                      <label htmlFor="otp">OTP Code</label>
                      <input
                        id="otp"
                        type="text"
                        value={otpCode}
                        onChange={(e) => setOtpCode(e.target.value)}
                        placeholder="Enter 6-digit code"
                        inputMode="numeric"
                        pattern="[0-9]*"
                        required
                      />
                    </div>
                  )}

                  <button type="submit" className="auth-button" disabled={loading}>
                    {loading ? 'Loading...' : (otpSent ? 'Verify Code' : 'Send Code')}
                  </button>
                </form>
                {!otpSent && (
                  <p className="auth-header" style={{ marginTop: '0.5rem' }}>
                    We'll email you a code or magic link
                  </p>
                )}
              </>
            )}
          </>
        )}

        <div className="auth-switch">
          <p>
            {isSignUp ? 'Already have an account?' : 'Need an account?'}
            <button 
              type="button"
              onClick={() => {
                setIsSignUp(!isSignUp);
                setError('');
                setMessage('');
                setOtpSent(false);
                setOtpCode('');
              }}
              className="auth-switch-btn"
            >
              {isSignUp ? 'Sign In' : 'Sign Up'}
            </button>
          </p>
        </div>
      </div>
    </div>
  );
}

export default Auth;
