import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import Navigation from '../components/Navigation';
import { generateRecipeUrl, generateMyRecipesUrl, generateCreateRecipeUrl, getUsernameForUrl } from '../utils/urlUtils';
import './HomePage.css';

function HomePage() {
  const [featuredRecipes, setFeaturedRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  const categories = [
    { id: 'quick-easy', name: 'Quick & Easy', image: '/api/placeholder/200/200', color: '#4a5568' },
    { id: 'healthy', name: 'Healthy', image: '/api/placeholder/200/200', color: '#38a169' },
    { id: 'desserts', name: 'Desserts', image: '/api/placeholder/200/200', color: '#9f7aea' },
    { id: 'vegetarian', name: 'Vegetarian', image: '/api/placeholder/200/200', color: '#ed8936' },
    { id: 'family', name: 'Family', image: '/api/placeholder/200/200', color: '#3182ce' },
    { id: 'holiday', name: 'Holiday', image: '/api/placeholder/200/200', color: '#d69e2e' }
  ];

  useEffect(() => {
    fetchRecipes();
  }, []);

  const fetchRecipes = async () => {
    setLoading(true);
    try {
      // Get current user
      const { data: { user: currentUser } } = await supabase.auth.getUser();
      setUser(currentUser);

      // Fetch featured recipes - limit to 4 for the featured section
      let query = supabase
        .from('recipes')
        .select(`
          id,
          title,
          category,
          image_url,
          created_at,
          ingredients,
          steps,
          is_public
        `)
        .eq('is_public', true)
        .order('created_at', { ascending: false })
        .limit(4);

      const { data, error } = await query;
      if (error) throw error;

      setFeaturedRecipes(data || []);
    } catch (error) {
      console.error('Error fetching recipes:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleRecipeClick = (recipe) => {
    // For public recipes table, navigate to a general recipe view
    navigate(`/recipe/${recipe.id}`);
  };

  return (
    <>
      <Navigation />
      <div className="homepage">
        <div className="hero-section">
          <div className="hero-content">
            <h1 className="hero-title">
              Discover & Share
              <span className="hero-highlight">Amazing Recipes</span>
            </h1>
            <p className="hero-subtitle">
              Join our community of food lovers and share your favorite recipes with the world
            </p>
            {user ? (
              <div className="hero-actions">
                <button 
                  className="btn btn-primary"
                  onClick={() => {
                    const username = getUsernameForUrl(user, null);
                    navigate(generateCreateRecipeUrl(username));
                  }}
                >
                  Create Recipe
                </button>
                <button 
                  className="btn btn-secondary"
                  onClick={() => {
                    const username = getUsernameForUrl(user, null);
                    navigate(generateMyRecipesUrl(username));
                  }}
                >
                  My Recipes
                </button>
              </div>
            ) : (
              <div className="hero-actions">
                <button 
                  className="btn btn-primary"
                  onClick={() => window.dispatchEvent(new CustomEvent('openAuth'))}
                >
                  Get Started
                </button>
              </div>
            )}
          </div>
        </div>

        <div className="recipes-section">
          <div className="container">
            <h2 className="section-title">Featured Recipes</h2>

            {loading ? (
              <div className="loading-state">
                <div className="loading-spinner"></div>
                <p>Loading recipes...</p>
              </div>
            ) : recipes.length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">üìù</div>
                <h3>No recipes yet</h3>
                <p>Be the first to share a recipe with our community!</p>
                {user && (
                  <button 
                    className="btn btn-primary"
                    onClick={() => navigate('/create-recipe')}
                  >
                    Create First Recipe
                  </button>
                )}
              </div>
            ) : (
              <div className="recipes-grid">
                {recipes.map(recipe => (
                  <div 
                    key={recipe.id} 
                    className="recipe-card"
                    onClick={() => handleRecipeClick(recipe)}
                  >
                    <div className="recipe-image-container">
                      {recipe.image_url ? (
                        <img 
                          src={recipe.image_url} 
                          alt={recipe.title}
                          className="recipe-image"
                          loading="lazy"
                        />
                      ) : (
                        <div className="recipe-image-placeholder">
                          <span>üç≥</span>
                        </div>
                      )}
                      <div className="recipe-overlay">
                        <h3 className="recipe-title">{recipe.title}</h3>
                        {recipe.category && (
                          <span className="recipe-category">{recipe.category}</span>
                        )}
                      </div>
                    </div>
                    <div className="recipe-footer">
                      <span className="recipe-date">
                        {new Date(recipe.created_at).toLocaleDateString()}
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </>
  );
}

export default HomePage;
